import { useRecoilValue } from "recoil";
import styled from "styled-components";
import { historyState } from "../atoms";

const Container = styled.div`
  max-width: 900px;
  margin: 0;
  padding: 5vh 0;
`;

const Row = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 50vw;
  margin-bottom: 0.5vw;
`;

const Cell = styled.div`
  border: none;
  background-color: #c4cbdd;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 4.5vw;
  height: 4.5vw;
  border-radius: 0.5vw;
  font-size: 1.5vw;
  color: #12121b;
  font-weight: 600;
`;

const Keyboard = () => {
  const history = useRecoilValue(historyState);
  return (
    <Container>
      <Row>
        <Cell>Q</Cell>
        <Cell>W</Cell>
        <Cell>E</Cell>
        <Cell>R</Cell>
        <Cell>T</Cell>
        <Cell>Y</Cell>
        <Cell>U</Cell>
        <Cell>I</Cell>
        <Cell>O</Cell>
        <Cell>P</Cell>
      </Row>
      <Row>
        <Cell>A</Cell>
        <Cell>S</Cell>
        <Cell>D</Cell>
        <Cell>F</Cell>
        <Cell>G</Cell>
        <Cell>H</Cell>
        <Cell>J</Cell>
        <Cell>K</Cell>
        <Cell>L</Cell>
      </Row>
      <Row>
        <Cell style={{ width: "7vw" }}>backspace</Cell>
        <Cell>Z</Cell>
        <Cell>X</Cell>
        <Cell>C</Cell>
        <Cell>V</Cell>
        <Cell>B</Cell>
        <Cell>N</Cell>
        <Cell>M</Cell>
        <Cell>Enter</Cell>
      </Row>
    </Container>
  );
};

export default Keyboard;
