import { useRecoilValue } from "recoil";
import styled from "styled-components";
import { historyState } from "../atoms";

const Container = styled.div`
  max-width: 900px;
  margin: 0;
  padding: 5vh 0;
`;

const Row = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 50vw;
  margin-bottom: 0.5vw;
`;

const Cell = styled.div`
  border: none;
  background-color: #c4cbdd;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 4.5vw;
  height: 4.5vw;
  border-radius: 0.5vw;
  font-size: 1.5vw;
  color: #12121b;
  font-weight: 600;
`;

const Keyboard = () => {
  const history = useRecoilValue(historyState);
  const firstRow = ["Q", "W", "E", "R", "T", "Y", "U", "I", "O", "P"];
  const secondRow = ["A", "S", "D", "F", "G", "H", "I", "J", "K", "L"];
  const thirdRow = ["Z", "X", "C", "V", "B", "N", "M"];

  return (
    <Container>
      <Row>
        {firstRow.map((element) => (
          <Cell>{element}</Cell>
        ))}
      </Row>
      <Row>
        {secondRow.map((element) => (
          <Cell>{element}</Cell>
        ))}
      </Row>
      <Row>
        <Cell style={{ width: "7vw" }}>backspace</Cell>
        {thirdRow.map((element) => (
          <Cell>{element}</Cell>
        ))}
        <Cell style={{ width: "7vw" }}>Enter</Cell>
      </Row>
    </Container>
  );
};

export default Keyboard;
