import { atom } from "recoil";

export enum Categories {
  "NOT_FINISHED",
  "WIN",
  "LOSE",
}

export const answerState = atom({
  key: "answer",
  default: "",
});

export const isFinishState = atom({
  key: "isFinish",
  default: "",
});
