import styled from "styled-components";
import { IResult, STATUS, answerState } from "./atoms";
import { useRecoilValue } from "recoil";

const Container = styled.div<IResult>`
  background-color: rgba(0, 0, 0, 0.8);
  top: ${(props) => (props.result === STATUS.NOT_FINISHED ? "-100vh" : 0)};
  width: 100vw;
  height: 100vh;
  position: fixed;
  transition: top 1s ease-in-out;
  color: white;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  h1 {
    font-size: 5vw;
    margin-bottom: 3vw;
  }
  div {
    display: flex;
    span {
      font-size: 7.5vw;
      width: 7vw;
      height: 7vw;
      font-weight: 700;
      margin-right: 1vw;
      background-color: #539165;
      text-align: center;
      vertical-align: center;
    }
  }
`;

const ResultPage = ({ result }: IResult) => {
  const answer = useRecoilValue(answerState);
  return (
    <Container result={result}>
      <h1>{result === STATUS.WIN ? "GOOD !!!" : "So Close..."}</h1>
      <h1>The Answer is...</h1>
      <div>
        <span>{answer[0].toUpperCase()}</span>
        <span>{answer[1].toUpperCase()}</span>
        <span>{answer[2].toUpperCase()}</span>
        <span>{answer[3].toUpperCase()}</span>
        <span>{answer[4].toUpperCase()}</span>
      </div>
    </Container>
  );
};

export default ResultPage;
