import { answerState, colorState } from "../atoms";
import { useRecoilState, useRecoilValue } from "recoil";
import Letter from "./Letter";

const Word = () => {
  const answer = useRecoilValue(answerState);
  const [color, setColor] = useRecoilState(colorState);

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    checkIsAnswer(event);
  };

  const checkIsAnswer = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    let checkArr = [];
    let res = [];
    let green = 0;
    for (let i = 0; i < 5; i++) {
      res.push((event.currentTarget[i] as HTMLInputElement).value);
      if (res[i] === answer[i]) {
        green++;
        checkArr[i] = "G";
      } else if (answer.includes(res[i])) {
        checkArr[i] = "Y";
      } else {
        checkArr[i] = "B";
      }
    }
    console.log(checkArr);
  };

  return (
    <form onSubmit={handleSubmit}>
      <Letter bgcolor="gray"></Letter>
      <Letter bgcolor="gray"></Letter>
      <Letter bgcolor="gray"></Letter>
      <Letter bgcolor="gray"></Letter>
      <Letter bgcolor="gray"></Letter>
      <button type="submit" style={{ display: "none" }} />
    </form>
  );
};

export default Word;
